CREATE OR REPLACE FUNCTION is_upd_executed(in_upd_name TEXT, in_upd_note TEXT, do_reg_exec BOOLEAN) RETURNS BOOLEAN AS$BODY_FUNC$DECLARE	v_upd_executed BOOLEAN := FALSE;BEGIN	-- Questo aggiornamento è già stato eseguito in passato?	SELECT INTO v_upd_executed		COUNT(*) = 1 AS upd_executed	FROM ortasmgr_upds.db_upds AS upd	WHERE upd.upd_name = in_upd_name;		IF(v_upd_executed = FALSE)THEN		IF(do_reg_exec = TRUE)THEN			-- REGISTRO l'esecuzione dell'aggiornamento db			INSERT INTO ortasmgr_upds.db_upds					(id     , upd_name  , data , note      ) 			VALUES	(DEFAULT, in_upd_name, now(),in_upd_note);		END IF;				RETURN FALSE;	END IF;		RETURN TRUE;END;$BODY_FUNC$LANGUAGE plpgsql VOLATILE;